package com.syntaxreview.class03;

public class remainderOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=12,b=5;
		String c="HI", d="IZ";
		// % remainder 
		System.out.println("Remainder "+a%b);
		
		
		System.out.println(a+b+c+d);//17HIIZ
		System.out.println(c+d+a+b);//HIIZ125
		System.out.println(c+d+(a+b));//HIIZ17
		//System.out.println(a+b+c+d);
		
		int myNumber=100;
		myNumber+=10;   //for reassign short way -+*/
		System.out.println(myNumber);
		
		
	}

}

