package com.syntaxreview.class08;

public class FORloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=1;i<=5; i++) {
	System.out.println("good night");
}
for (int a=1; a<=100;a++) {
	System.out.print(a+" ");
}System.out.println("");
for (int a=10; a>=0;a--) {
	System.out.print(a+" ");
}
System.out.println();
for (int a=0; a<=30;a+=3) {
	System.out.print(a+" ");
}
	}

}
