package com.syntaxreview.class09;

public class Patterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=0;i<=5;i++) {
	
for (int j=0;j<=11;j++) {
		System.out.print("*");
	}System.out.println("");
}
for (int i=1;i<=7;i++) {
	
for (int j=7;j>=1;j--) {
		System.out.print(j+" ");
	}System.out.println("");
}
	}

}
